import React, { useState } from "react";
import { useEffect } from "react";
import { Card } from "../UI/Card";

import classes from "./AvailableMeals.module.css";
import { Mealitem } from "./MealItem/Mealitem";



export const AvailableMeals = () => {

  const [meals,setMeals] = useState([]);
  

  // useEffect(()=>{
  //            const fetchmeals = async () => {
  //               const response = await fetch('https://react-http-9a53a-default-rtdb.firebaseio.com/meals.json');
  //               const resposeData =await response.json();
  //               const LoadedMeals = [] ;

  //               for(const key in resposeData) {
  //                 LoadedMeals.push({
  //                   id:key,
  //                   name:resposeData[key].name,
  //                   description:resposeData[key].description,
  //                   price:resposeData[key].price
  //                 })
  //               }
  //               setMeals(LoadedMeals);
  //            }

  //            fetchmeals();

  // },[])

  useEffect(() => {
         const fetchmeals = async() => {

          const response= await fetch('https://react-http-9a53a-default-rtdb.firebaseio.com/meals.json');
          const responseData = await response.json();

          const LoadedMeals = [] ;

          for(const key in responseData) {
            LoadedMeals.push ({
              id:key,
              name:responseData[key].name,
              description:responseData[key].description,
              price:responseData[key].price
            })
          }
          setMeals(LoadedMeals);
         } 
  },[])

  

  const mealsList = meals.map((meal) => (
    <Mealitem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};
